    <script 
      src="https://cdnjs.cloudflare.com/ajax/libs/jspdf/1.3.4/jspdf.min.js" 
      integrity="sha256-vIL0pZJsOKSz76KKVCyLxzkOT00vXs+Qz4fYRVMoDhw="
      crossorigin="anonymous">
    </script>

    <script>
      function validateDoctorPassword() {
        var password, text, generateButtom;

        generateButtom = document.getElementById("generate");
        password = document.getElementById("doctorPassword").value;
        console.log('o generatebuttom fora', generateButtom);

        if( password != '12345'){
          text = "Senha inválida";
          return document.getElementById("demo").innerHTML = text;
        } else {
        console.log('o generatebuttom dentro', generateButtom);
          generateButtom.disabled = false;
          text = "";
          document.getElementById("demo").innerHTML = text;
          return  true;
        }
      }

      function generateSickNote() {
        var nome, idade, altura, peso, cpf, especialidade, horario, dayOff, obs;

        nome = document.getElementById("patientName").value;
        idade = document.getElementById("patientAge").value;
        identidade = document.getElementById("patientIdentity").value;
        peso = document.getElementById("patientWeight").value;
        cpf = document.getElementById("patientCPF").value;
        obs = document.getElementById("comment").value;
        daysOff = document.getElementById("daysOff").value;
        codigoAtestado = Math.random();
        now = new Date;
        dia = now.getDate(); 
        var d = new Date();
        var month = new Array();
        month[0] = "Janeiro";
        month[1] = "Fevereiro";
        month[2] = "Março";
        month[3] = "Abril";
        month[4] = "Maio";
        month[5] = "Junho";
        month[6] = "Julho";
        month[7] = "Agosto";
        month[8] = "Setembro";
        month[9] = "Outubro";
        month[10] = "Novembro";
        month[11] = "Dezembro";
        var mes = month[d.getMonth()];
        ano = now.getFullYear();
        horario = now.getHours();
        especialidade =  'Cardiologia';

        var doc = new jsPDF()

        //declarando as imagens abaixo

        var logoAtestar = 'data:image/png;base64,'
        
        var logoClinica = 'data:image/jpeg;base64,'


        doc.rect(10, 8, 190, 268)

        doc.setFont('times')
        doc.setFontSize(16)
        doc.setFontType('italic')
        doc.addImage(logoClinica, 'JPEG', 127, 15, 60, 30)
        doc.text(20, 32, 'Clínica Company - Instituto Integrado')
        doc.line(19, 34, 120, 34) // horizontal line
      
        doc.setFont('helvetica')
        doc.setFontSize(12)
        doc.text(20, 62, `Declaro que o(a) Sr(a).: `)

        doc.setFontType('bold')
        doc.text(66, 62, `${nome},`)

        doc.setFontType('normal')
        doc.text(20, 69, `portador(a) do número de documento de identidade.: ${identidade}`)
        doc.text(20, 76, `e do CPF.: ${cpf}, compareceu no dia ${dia} de ${mes} do ano de ${ano}`)
        doc.text(20, 83, `às ${horario}:00 horas para consulta da especialidade ${especialidade}.`)
        doc.text(20, 90, `A consulta foi realizada com o Dr. João Silva CRM.: 33.2129.213.25.`)

        doc.text(20, 97, `O/A referido possui, à partir da data atual, ${daysOff} dias de afastamento de qualquer atividade`)
        doc.text(20, 104, `que exija esforço mínimo do paciente para sua plena recuperação.`)

        doc.setFontType('bold')
        doc.text(20, 117, `Observações.:`)
        doc.setFontSize(10)
        doc.setFontType('italic')
        doc.text(20, 121, `${obs}`)

        doc.setFont('helvetica')
        doc.setFontSize(10)
        doc.setFontType('normal')
        doc.text(20, 237, `Este documento foi emitido digitalmente e não precisa de assinatura física. Para constatar sua`)
        doc.text(20, 244, `veracidade, acesse o site WWW.ATESTARAPP.COM.BR e no campo "Código do atestado"`)
        doc.text(20, 251, `digite o código a seguir: ${codigoAtestado}. `)
        doc.addImage(logoAtestar, 'JPEG', 170, 248, 20, 10)

        


        doc.save();
        


        // $this->PacientController::insertNewPacient(nome, idade, altura, peso, cpf);
        // $this->PacientController::generateNewSickNote(nome, idade, altura, peso, cpf, codigoAtestado);
        // tipoAtendimento = document.getElementById("tipoAtendimento").value;
      }

